import React,{useState} from 'react';
import './Signup.css';
import { useNavigate } from 'react-router-dom';

function createUser(event,username, email,password,confirm_password,navigate){
    event.preventDefault();
    if (password!==confirm_password){
        alert('Password do not match');
        return;
    }
    
    navigate("/home");
}

function Signup(){
    const [username,setUsername]=useState('');
    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');
    const [confirm_password,setConfirmPassword]=useState('');
    const navigate=useNavigate();
    return (
        <>
            <header>
                <h1 id="header-signup"> MPT Fitness App</h1>
            </header>
            <div className="container">
                <form onSubmit={(event) => createUser(event,username,email,password,confirm_password,navigate)}>
                    <div className="username">
                        <label htmlFor="username">Username: </label>
                        <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} required/>
                    </div>
                    <div className="email">
                        <label htmlFor="email">Email: </label>
                        <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
                    </div>
                    <div className="password">
                        <label htmlFor="password">Password:</label>
                        <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
                    </div>
                    <div className="empty">
                        <label id="empty">&nbsp</label>
                        <p>At least 8 to 20 characters</p>
                    </div>
                    <div className="confirm_password">
                        <label htmlFor="confirm_password">Confirm Password:</label>
                        <input type="password" id="confirm_password" value={confirm_password} 
                        onChange={(e) => setConfirmPassword(e.target.value)} required/>
                    </div>
                    <div className="empty">
                        <label id="empty">&nbsp</label>
                        <p>At least 8 to 20 characters</p>
                    </div>
                    <div className="button">
                        <button id='signup' type="submit">Sign Up</button>
                    </div>
                </form>
            </div>
        </>
    );
}

export default Signup;
